function(pageData, pageUrl) {
	var iUrl = pageData.match(/img src="(.+?serve3x.+?)"/);
	return iUrl ? {imgUrl: iUrl[1], fileName: iUrl[1].replace(/\.jpe?g/i, ".jpg"), status: "OK"} : {imgUrl: null, status: "ABORT"}
}

function(pageData, pageUrl) {
	var id = pageUrl.match(/https?:\/\/[^/]+\/([^/\.]+)/);
	var req = new XMLHttpRequest();
	req.open("POST", pageUrl, false);
	req.setRequestHeader("Content-Type", "application/x-www-form-urlencoded; charset=UTF-8");
	req.send("op=view&id="+id[1]+"&pre=1&next=Download Now");
	var iUrl = (req.responseText).match(/src=("|')(https?:\/\/[^/]+\/(img|i)\/.+?)\1.+alt=("|')(.+\.jpe?g)\4/i);
	return iUrl ? {imgUrl: iUrl[2], fileName: iUrl[5].replace(/\.jpe?g/i, ".jpg"), status: "OK"} : {imgUrl: null, status: "ABORT"}
}

