function(pageData, pageUrl) {
	var hUrl = pageUrl.substr(0,pageUrl.indexOf('/',pageUrl.indexOf('.')));
	var iUrl = pageData.match(/SRC="(\/pictures\/)(?:\w+\/)?small\/(.+?)"/);
	return iUrl ? {imgUrl: hUrl + iUrl[1] + 'max/' + iUrl[2], status: "OK"} : {imgUrl: null, status: "ABORT"}
}

function(pageData, pageUrl) {
	var jsonar = pageData.match(/"images":\[(\{.+?\},?)+?\]/);
	var obj = JSON.parse(jsonar[1]);
	return obj? {imgUrl: obj.url, status: "OK"} : {imgUrl: null, status: "ABORT"}
}

