function(pageData, pageUrl) {
	var retVal = {};
	var sPattern = pageData.match(/window.location="(.+)"/);

	if (!sPattern) {
		var sPattern2 = pageData.match(/https?:\/\/img\d+\.cocoimage\.com\/showimg\.php\?id=\d+&v=\d+&ext=jpg/);

		if(!sPattern2) {
			retVal.imgUrl = null;
			retVal.status = "ABORT";
			}
		else {
			retVal.imgUrl = sPattern2[0] + "&dt=1";
			retVal.status = "OK";

			try {
				retVal.fileName = sPattern2[0].match(/id=(\d+)/)[1] + ".jpg";
				}
			catch(e) {
				retVal.fileName = Math.random().toString().substring(2) + ".jpg";
				}
			}
		}
	else {
		retVal.imgUrl = sPattern[1];
		retVal.status = "REQUEUE";
		}

	return retVal;
	}

function(pageData, pageUrl) {
	var retVal = {};

	var iUrl = pageData.match(/<img src='(.+?)'/);

	if (!iUrl) {
		retVal.imgUrl = null;
		retVal.status = "ABORT";
		}
	else {
		retVal.imgUrl = iUrl[1];
		retVal.status = "OK";
		}

	return retVal;
	}

