function(pageData, pageUrl) {
	var hUrl = pageUrl.match(/https?:\/\/(?:www\.)?.+?\/(?:\w+\/)?/);
	var iUrl = pageData.match(/src="(albums\/[^\"]+)normal_([^\"]+)"/);
	if (!iUrl) iUrl = pageData.match(/src="(albums\/[^\"]+)([^\"]+)"/);
	return iUrl ? {imgUrl: hUrl + iUrl[1] + iUrl[2], status: "OK"} : {imgUrl: null, status: "ABORT"}
}

