function(pageData, pageUrl) {
	var id = pageUrl.match(/https?:\/\/[^/]+\/([^/\.]+)$/);
	var req = new XMLHttpRequest();
	req.open("POST", pageUrl, false);
	req.setRequestHeader("Content-Type", "application/x-www-form-urlencoded; charset=UTF-8");
	req.send("op=view&amp;id="+id[1]+"&amp;pre=2&amp;next=Continue to image.");
	var iUrl = (req.responseText).match(/img src=\"(.+?)\" class=\"pic\" alt=\"(.+?)\"/i);
	return iUrl ? {imgUrl: iUrl[1], fileName: iUrl[2], status: "OK"} : {imgUrl: null, status: "ABORT"}
}

