function(pageData, pageUrl) {
	var retVal = {imgUrl: null, status: "ABORT"};

	var iUrl = pageData.match(/<div class="entry-content-img(?:.|\n)+?<img[^>]+src="([^"]+)"/);
	if (!iUrl) iUrl = pageData.match(/<div class="slider_container(?:.|\n)+?<img[^>]+src="([^"]+)"/);

	if (iUrl) {
		if (pageUrl.match(/full-size\/?$/))
			//retVal.imgUrl = iUrl[1];
			retVal.imgUrl = iUrl[1].replace(/^(.+)-\d+x\d+(\..{3,4})$/, "$1$2");
		else
			retVal.imgUrl = iUrl[1].replace(/^(.+)-\d+x\d+(\..{3,4})$/, "$1$2");
		retVal.status = "OK";
	}

	return retVal;
}

