function(pageData, pageUrl) {
	var retVal = {imgUrl: null, status: "ABORT"};

	var iUrl = null;

	var theId = "img";
	var searchPat = new RegExp("<img[^>]+id\\s*=\\s*(\"|')?" + theId + "\\1(?:.|\\s)+?>", "i");
	var tmpMatch = pageData.match(searchPat);
	if (tmpMatch) {
		tmpMatch = tmpMatch[0];
		iUrl = tmpMatch.match(/src\s*=\s*("|')?(.+?)\1/i);
		if (iUrl) iUrl = iUrl[2];
	}
	if (!iUrl) {
		iUrl = pageData.match(/src="([^"]+\/keystamp=[^"]+)"/);
		if (iUrl) iUrl = iUrl[1];
	}

	if (iUrl) {
		retVal.imgUrl = iUrl;
		retVal.status = "OK";
	}

	return retVal;
}

