function(pageData, pageUrl) {
	var t = pageUrl.indexOf("m.facebook.com")
	if (t != -1) {
		var u = pageData.match(/href="(http[^"]+)"[ ]*>View Full Size/i);
		return u ? {imgUrl: u[1].replace(/&quot;/g, "\"").replace(/&gt;/g, ">").replace(/&lt;/g, "<").replace(/&aqos;/g, "'").replace(/&amp;/g, "&"), status: 'OK'} : {imgUrl: null, status: 'ABORT'}
	}
	return t ? {imgUrl: pageUrl.replace(/www\./,"m."), status: 'REQUEUE'} : {imgUrl: null, status: 'ABORT'}
}

function(pageData, pageUrl) {
	var retVal = {};
	var iUrl =null;
	var x = pageUrl.match(/read#page=(\d+)/);
	if (x) {
		// Gross way of padding the integer
		x = '' + x[1];
		while (x.length < 3) x = '0' + x;

		iUrl = pageData.match(/return\s*\'(https:\/\/t\.fakku.net\/images\/.+\/images\/)\'\s*\+\s*x\s*\+\s*\'(\.\w+)/);
		if (iUrl) iUrl[1] = iUrl[1] + x + iUrl[2];
	}

	if (!iUrl) {
		retVal.imgUrl = null;
		retVal.status = "ABORT";
		}
	else {
		retVal.imgUrl = iUrl[1];
		retVal.status = "OK";
		}

	return retVal;
}

function(pageData, pageUrl) {
	var retVal = {};

	var iDiv = pageData.match(/<div id=("|')photo_frame\1>(?:.|\s)+?<\/div>/);
	var iImgTag = iDiv.toString().match(/<img.+src=("|')(.+)?\1/);

	if (iImgTag) var iUrl = iImgTag[2];

	if (!iUrl) {
		retVal.imgUrl = null;
		retVal.status = "ABORT";
		}
	else {
		retVal.imgUrl = iUrl;
		retVal.status = "OK";
		}

	return retVal;
	}

function(pageData, pageUrl) {
	var retVal = {};

	var user = pageUrl.match(/user\/(.+?)\//);
	if (user) {
		var user = user[1];
		var someRE = new RegExp("\"(https?:\\/\\/.+\\/big\\/.+?\\/.+?\\/.+?\\/" + user + "\\/" + user + ".+?)\"");
		var iUrl = pageData.match(someRE);
		if (iUrl) iUrl = iUrl[1];
		}

	if (!iUrl) {
		retVal.imgUrl = null;
		retVal.status = "ABORT";
		}
	else {
		retVal.imgUrl = iUrl;
		retVal.status = "OK";
		}

	return retVal;
	}

