function(pageData, pageUrl) {
	var retVal = {imgUrl: null, status: "ABORT"};
	var sizes = pageData.match(/"sizes":{.+?}}/);
	if (sizes) {
		var url = sizes[0].match(/"url":"[^"]+/ig);
		if (url) {
			retVal.imgUrl = url[url.length - 1].replace(/\\/g, '').substring(7);
			retVal.status = "OK";
		}
	}
	return retVal;
}

