function(pageData, pageUrl) {
	var ind = pageUrl.match(/.+?#(\d+)/);
	var rank = "", czer = "", fzer = "", sol = "", mUrl = "";
	var aUrl = pageData.match(/\/\/\w\.hitomi\.la\/galleries\/(.+?)\d+\.jpg/gi);
	czer = aUrl[0].match(/.+(\/\d+\.)jpg/);
	if (! czer) czer = aUrl[0].match(/.+(_\d+\.)jpg/);
	fzer = czer[1].length - 3;

	if (fzer == 0) rank = ind[1];
	else if (ind[1].length+2 == fzer) rank = "000" + ind[1];
	else if (ind[1].length+1 == fzer) rank = "00" + ind[1];
	else if (ind[1].length == fzer) rank = "0" + ind[1];
	else if (ind[1].length == fzer+1) rank = ind[1];

	var iUrl = pageData.match(/(\/\/\w\.hitomi\.la\/galleries\/.+?\d+\.jpg)/);
	mUrl = pageData.match(/\/\/\w\.hitomi\.la\/galleries\/(.+?)\d+\.jpg/);
	sol = "https://la.hitomi.la/galleries/" + mUrl[1] + rank + ".jpg";

	var req = new XMLHttpRequest();
	req.open("POST", sol, false);
	req.setRequestHeader("Content-Type", "application/x-www-form-urlencoded; charset=UTF-8");
	req.send("");

	if(req.status != 200 && req.status != 405) {
		mUrl = pageData.match(/\/\/\w\.hitomi\.la\/galleries\/(.+?)\d+\.png/);
		sol = "https://la.hitomi.la/galleries/" + mUrl[1] + rank + ".png";
		}

	return iUrl ? {imgUrl: sol, status: "OK"} : {imgUrl: null, status: "ABORT"}
}

