		retVal.imgUrl = pageUrl;
		retVal.status = "REQUEUE";
		}
	else {
		var theId = "image";
		var searchPat = new RegExp("<.+id(?:\\s+)?=(?:\\s+)?(\"|')?" + theId + "\\1.+?>");
		var tmpMatch = pageData.match(searchPat);
		if (tmpMatch) {
			tmpMatch = tmpMatch[0];
			var iUrl = tmpMatch.match(/src(?:\s+)?=(?:\s+)?("|')?(.+?)\1/);
			if (iUrl) iUrl = iUrl[2];
			}

		if (!iUrl) {
			retVal.imgUrl = null;
			retVal.status = "ABORT";
			}
		else {
			retVal.imgUrl = iUrl;
			retVal.status = "OK";
			}
		}

	return retVal;
	}

