function(pageData, pageUrl) {
	var retVal = {};
	var assCleaner = pageData.match(/var url_image_path = '(.+)';\s*/);
	if (!assCleaner) {
		assCleaner = pageData.match(/<link.+rel=('|")image_src\1.+href=('|")(.+?)\2.+>/);
		if (!assCleaner) assCleaner = pageData.match(/<img.+id=('|")main_image\1.+src=('|")(.+?)\2.+>/);
		if (!assCleaner) {
			retVal.imgUrl = null;
			retVal.status = "ABORT";
			}
		else {
			retVal.imgUrl = assCleaner[3];
			retVal.status = "OK";
			}
		}
	else {
		retVal.imgUrl = pageUrl.match(/https?:\/\/img\d+.imageshack.us\//) + assCleaner[1];
		retVal.status = "OK";
		}
	return retVal;
	}

