function(pageData, pageUrl) {
	var retVal = {imgUrl: null, fileName: null, status: "ABORT"};
	var prot = pageUrl.match(/^(.+?)(:\/\/.+)$/);
	var sec = "https" + prot[2];
	var req = new XMLHttpRequest();
	req.open("POST", sec, false);
	req.setRequestHeader("Content-Type", "application/x-www-form-urlencoded; charset=UTF-8");
	req.send("imgContinue=Continue to image ...");
	// Search for <img ... class="centred" ... >
	var iTag = (req.responseText.match(/<img\b[^>]*?\bclass\s*=\s*('|")centred\1[^>]*?>/i)||[])[0];
	if (iTag) {
		var iUrl  = (iTag.match(/\bsrc\s*=\s*('|")(.*?)\1/i)||[])[2];
		var iName = (iTag.match(/\balt\s*=\s*('|")(.*?)\1/i)||[])[2];
		if (iUrl) {
			retVal.imgUrl = iUrl;
			retVal.status = "OK";
			if (iName) { // exists and is not empty
				retVal.fileName = iName;
				var extension = (iUrl.match(/^(.*?)(\.([^./]*))?$/)||[])[3];
				if (extension) retVal.fileName += "." + extension;
			}
		}
	}
	return retVal;
}

