			if (iName) {  // exists and is not empty
				retVal.fileName = iName;
				var extension = (iUrl.match(/^(.+?)(\.([^./]*))?$/)||[])[3];
				if (extension) retVal.fileName += "." + extension;
			} else {
				iName = (pageUrl.match(/_([^/]*)\.html/i)||[])[1];
				if (iName) retVal.fileName = iName;
			}
		}
	}
	return retVal;
}

function(pageData, pageUrl) {
	var retVal = {imgUrl: null, status: "ABORT"};

	var iUrl = pageData.match(/<a href="([^"]+)">Continue to your image<\/a>/);

	if (iUrl) {
		retVal.imgUrl = iUrl[1];
		retVal.status = "REQUEUE";
		}

	return retVal;
	}

