function(pageData, pageUrl) {
	var retVal = {imgUrl: null, fileName: null, status: "ABORT"};
	// Search for <img ... id="knjdycbs87nbd" ... >
	var iTag = (pageData.match(/<img\b[^>]*?\bid\s*=\s*('|")knjdycbs87nbd\1[^>]*?>/i)||[])[0];
	// Fallback: Search for <img ... onload ... >
	if (!iTag) iTag = (pageData.match(/<img\b[^>]*?\bonload[^>]*?>/i)||[])[0];
	if (iTag) {
		var iUrl = (iTag.match(/\bsrc\s*=\s*('|")(.*?)\1/i)||[])[2];
		if (iUrl) {
			retVal.imgUrl = iUrl;
			retVal.status = "OK";
			var iName = (pageData.match(/\bFilename:.+?nowrap>([^<]*?)</i)||[])[1];
			if (!iName) iName = (pageUrl.match(/\/([^/]*)\.html/i)||[])[1];
			if (iName) {  // exists and is not empty
				retVal.fileName = iName;
			}
		}
	}
	return retVal;
}

