function(pageData, pageUrl) {
	var retVal = {imgUrl: null, fileName: null, status: "ABORT"};
	var req = new XMLHttpRequest();
	req.open("POST", pageUrl, false);
	req.setRequestHeader("Content-Type", "application/x-www-form-urlencoded; charset=UTF-8");
	// Extract PostData from form
	var PostData = (pageData.match(/<form\b[^>]*?>([\s\S]*?)<\/form>/i)||[,""])[1];
	PostData = PostData.match(/<input\b[^>]*?>/ig)||[""];
	PostData = PostData.map(function(input){return (input.match(/name=('|")(.*?)\1/)||[])[2] + '=' + (input.match(/value=('|")(.*?)\1/)||[])[2]}).join('&');
	req.send(PostData);
	// Search for <img ... class="pic" ... />
	var iTag = (req.responseText.match(/<img\b[^>]*?\bclass\s*=\s*('|")pic\1[^>]*?>/i)||[])[0];
	if (iTag) {
		var iUrl  = (iTag.match(/\bsrc\s*=\s*('|")(.*?)\1/i)||[])[2];
		var iName = (iTag.match(/\balt\s*=\s*('|")(.*?)\1/i)||[])[2];
		if (iUrl) {
			retVal.imgUrl = iUrl;
			retVal.status = "OK";
			if (iName) {  // exists and is not empty
				retVal.fileName = iName;
			}
		}
	}
	return retVal;
}

