function(pageData, pageUrl) {
	var theId = "og:image";
	var searchPat = new RegExp("<meta(?:\\s+)?property(?:\\s+)?=(?:\\s+)?(\"|')?" + theId + "\\1.+?>");
	var tmpMatch = pageData.match(searchPat);

	if (tmpMatch) {
		tmpMatch = tmpMatch[0];
		var the_url = tmpMatch.match(/content(?:\s+)?=(?:\s+)?("|')?(.+?)\1/);
		if (the_url) the_url = the_url[2];
	}

	var retVal = new Object();

	if(!the_url) {
		retVal.imgUrl = null;
		retVal.status = "ABORT";
	} else {
		retVal.imgUrl = the_url;
		retVal.status = "OK";
	}
	return retVal;
}

function(pageData, pageUrl) {
	var iUrl = pageData.match(/<meta name="og:image" content="([^"]+)"/);
