		photo_div = photo_div[0];
		var iUrl = photo_div.match(/(?:.|\s)+?<a href="(.+?)".+?<img src="(.+?)"/);
		if (!iUrl) var directUrl = photo_div.match(/(?:.|\s)+?<img src="(.+?)"/);
	}

	if (directUrl) {
		retVal.imgUrl = directUrl[1];
		retVal.status = "OK";
		return retVal;
	}

	if (!iUrl) {
		retVal.imgUrl = null;
		retVal.status = "ABORT";
		}
	else {
		if (iUrl[1].search(/^https?:\/\/.+\.media\.tumblr\.com\/.+/) >= 0 ||
			iUrl[1].search(/^https?:\/\/nsfworld\.com\/photo\/\d+\/\d+\/.+/) >= 0) {
			retVal.imgUrl = iUrl[1];
			retVal.status = "OK";
			}
		else {
			retVal.imgUrl = iUrl[2];
			retVal.status = "OK";
			}
		}

	return retVal;
	}

