		retVal.status = "ABORT";
		}
	else {
		retVal.imgUrl = sPattern[2];
		retVal.status = "OK";

		sPattern = pageData.match(/<title>Pic-Upload\.de\s*-\s*(.+)<\/title>/i);
		try {
			retVal.fileName = sPattern[1];
