function(pageData, pageUrl) {
	var retVal = {imgUrl: null, fileName: null, status: "ABORT"};
	var iUrl = (pageData.match(/property\s*=\s*('|")og:image\1\s*content\s*=\s*('|")(.*?)\2/i)||[])[3];
	if (iUrl) {
		retVal.imgUrl = iUrl;
		retVal.status = "OK";
		var iName = (pageData.match(/property\s*=\s*('|")og:title\1\s*content\s*=\s*('|")(.*?)\2/i)||[])[3];
		if (iName) {  // exists and is not empty
			if (iName.match(/\s*hosted\s*at.*$/i)) {
				iName = iName.replace(/\s*hosted\s*at.*$/i, "");
			} else {
				iName = iName.replace(/\s/g, "_"); // replace all spaces by underscores
				var extension = (iUrl.match(/^(.+?)(\.([^./]*))?$/)||[])[3];
				if (extension) iName += "." + extension;
			}
			retVal.fileName = iName;
		}
	}
	return retVal;
}

