	var newPageUrl = pageData.match(/linkDestUrl = '(.+?)'/);

	if (!newPageUrl) {
		retVal.status = "ABORT";
		retVal.imgUrl = null;
		}
	else {
		retVal.status = "REQUEUE";
		retVal.imgUrl = newPageUrl[1];
		}

	return retVal;
	}

function(pageData, pageUrl) {
	var iUrl;

	if (!pageUrl.match(/\/full\//)) {
		iUrl = pageData.match(/<a href='(.+?)'>full size/);
		if (iUrl) return {imgUrl: iUrl[1], status: "REQUEUE"}
	}

	iUrl = pageData.match(/src=('|")(https?:\/\/[^/]+\/.+?\.jpe?g)\1/i);
	return (iUrl) ? {imgUrl: iUrl[2], status: "OK"} : {imgUrl: null, status: "ABORT"}
}

function(pageData, pageUrl) {
	var iUrl = "";
	if (pageData.match(/data-full/)) {
		iUrl = pageUrl.match(/^https?:/) + pageData.match(/data-full="(.+?)"/)[1];
	} else {
		if (pageData.match(/Download original/)) {
			iUrl = pageData.match(/href="(.+?)\?.+?".+?fa-download/)[1];
		} else {
			iUrl = pageData.match(/src="(https?:\/\/.+?\.jpg)"/)[1];
		}
	}
	return iUrl ? {imgUrl: iUrl, status: "OK"} : {imgUrl: null, status: "ABORT"}
}

