function(pageData, pageUrl) {
	var num = pageUrl.match(/m(\d+)/);
	if (! num) {num = pageUrl.match(/a(\d+)/);}
	var iUrl = pageData.match('data-src="(.+?' + num[1] + '.+?)"');
	if (! iUrl) {iUrl = pageData.match('src="(.+?' + num[1] + '.+?)"');}
	var res = "";
	var sol = iUrl[1].match(/(.+?)114x114(-\d+\.jpg)/);
	if (! sol) {res = iUrl[1];} else {res = sol[1] + "950x0" + sol[2];}
	return iUrl ? {imgUrl: res, status: "OK"} : {imgUrl: null, status: "ABORT"}
}

