function(pageData, pageUrl) {
	var retVal = {};

	var shit = pageData.match(/var \w+(?:.|\s)+?var \w+=([^;]+?);/);
	if (!shit) {
		retVal.imgUrl = null;
		retVal.status = "ABORT";
		return retVal;
		}

	shit = shit[1].replace(/[\+\n"]/g, "");
	var daChar = shit.match(/^\d+(.)/);

	var sshit = shit.split(daChar[1]);
	var clean = "";
	for (var i = 0; i < sshit.length; i++){
		clean += String.fromCharCode(parseInt(sshit[i]));
	}

	var iUrl = clean.match(/top\.frames\.length(?:.|\n)+?url = '(.+?)'/);

	if (!iUrl) {
		retVal.imgUrl = null;
		retVal.status = "ABORT";
		}
	else {
		retVal.imgUrl = "http://refn.ru" + iUrl[1];
		retVal.status = "REQUEUE";
		}

	return retVal;
	}

