function(pageData, pageUrl) {
	var retVal = {};

	var srcPat = "https?:\\/\\/images\\.shareapic\\.net\\/images(\\d+)\\/(.+?)";

	var searchPat = new RegExp("<img.+src(?:\\s+)?=(?:\\s+)?(\"|')?(" + srcPat + ")\\1(?:.+?)?>");
	var tmpMatch = pageData.match(searchPat);

	if (tmpMatch) var the_url = "http://images.shareapic.net/fullsize"+ tmpMatch[3] + "/" + tmpMatch[4];

	if (!the_url) {
		retVal.status = "ABORT";
		retVal.imgUrl = null;
		}
	else {
		retVal.status = "OK";
		retVal.imgUrl = the_url;
		}
	return retVal;
	}

function(pageData, pageUrl) {
	var retVal = {};

	var theId = "img";

	var searchPat = new RegExp("<.+id(?:\\s+)?=(?:\\s+)?(\"|')?" + theId + "\\1.+?>");
	var tmpMatch = pageData.match(searchPat);
	if (tmpMatch) {
		tmpMatch = tmpMatch[0];
		var the_url = tmpMatch.match(/src(?:\s+)?=(?:\s+)?("|')?(.+?)\1/);
		if (the_url) the_url = the_url[2];
		}

	if (the_url) {
		if (the_url.match(/https?:\/\/img\d+\.hotlinkimage\.com\/showimg\.php\?id=\d+&v=\d+&ext=\w+$/)) {
			retVal.imgUrl = the_url.replace(/show(img\.php)/, "$1");
			retVal.status = "REQUEUE";
			return retVal;
			}
		}

	if (!the_url) {
		retVal.imgUrl = null;
		retVal.status = "ABORT";
		}
	else {
		retVal.imgUrl = the_url;
		retVal.status = "OK";
		}

	return retVal;
	}

function(pageData, pageUrl) {

	if (pageUrl.match(/\/gallery\//))
		if (!pageUrl.match(/\/original\/?$/))
			return {imgUrl: pageUrl.replace(/\/$/, '') + "/original", status: "REQUEUE"}

	if (pageData.match(/href=".+?\/original"/)) {
		var newUrl = pageUrl.match(/(https?:\/\/.+?)\//)[1] + pageData.match(/href="(.+?\/original)"/)[1];
		return {imgUrl: newUrl, status: "REQUEUE"}
	}

	var iUrl = pageData.match(/src="(https?:\/\/cache\.sharenxs\.com\/images\/.+?)" id=img1/);
	if (!iUrl) iUrl = pageData.match(/src="(https?:\/\/(?:www\.)?sharenxs\.com\/images\/.+?)"/);
	if (!iUrl) iUrl = pageData.match(/src="(https?:\/\/images\.sharenxs\.com\/.+?)"/);
	if (!iUrl) iUrl = pageData.match(/<img(?: class="view_photo")? src="(\/(?:images|photos)\/[^"]+)"/);
	if (iUrl) return {imgUrl: iUrl[1], status: "OK"}

	iUrl = pageData.match(/href="(\/view\/\?id=[^"]+&size=large)"/);
	if (!iUrl) iUrl = pageData.match(/href="(\/view\/\?id=[^"]+&offset=original)"/);
	if (!iUrl) iUrl = pageData.match(/href="(\/gallery\/[^\/]+\/[^\/]+\/original)"/);
	return iUrl ? {imgUrl: "http://sharenxs.com" + iUrl[1], status: "REQUEUE"} : {imgUrl: null, status: "ABORT"}
}

