function(pageData, pageUrl) {
	var retVal = {};
	var sUrl  = pageUrl.match(/https?:\/\/.+?\//) + "rpc/gallery.mg";
	var postArray = {};
	postArray.getNewPage = "false";
	postArray.pageOn = pageData.match(/pageOn = ("|')?(.*)\1;/)[2];
	postArray.pageType = pageData.match(/pageType = ("|')?(.*)\1;/)[2];
	postArray.AlbumID = pageUrl.match(/(\d+)_(.{5,7})#/)[1];
	postArray.AlbumKey = pageUrl.match(/(\d+)_(.{5,7})#/)[2];
	postArray.ImageID = pageUrl.match(/#(\d+)_(.{5,7})/)[1];
	postArray.siteUser = pageData.match(/siteUser = ("|')?(.*)\1;/)[2];
	postArray.pageScope = pageData.match(/pageScope = ("|')?(.*)\1;/)[2];
	postArray.community = pageData.match(/community = ("|')?(.*)\1;/)[2];
	postArray.pageDrawBy = pageData.match(/pageDrawBy = ("|')?(.*)\1;/)[2];
	postArray.pageTypeDetails = pageData.match(/pageTypeDetails = ("|')?(.*)\1;/)[2];
	postArray.galleryStyle = pageData.match(/galleryStyle = ("|')?(.*)\1;/)[2];
	postArray.photosPerPage = "15";
	postArray.photoSizes = "all";
	postArray.thumbSize = "Tiny";
	postArray.mainPhotoSize = "Medium";
	postArray.lightboxSize = "XLarge";
	var postData = "tool=ajaxGallery";
	for (var i in postArray) { postData += "&" + i + "=" + postArray[i]; }
	var req = new XMLHttpRequest();
	req.open("POST", sUrl, false);
	req.setRequestHeader("Content-Type", "application/x-www-form-urlencoded; charset=UTF-8");
	req.setRequestHeader("X-Requested-With", "XMLHttpRequest");
	req.send(postData);

	var someData = req.responseText;

	var searchPat = new RegExp("\"Images\".+?{(\"ImageID\":" + postArray.ImageID + ".+?)}");
	var imageInfo = someData.match(searchPat);
	if (imageInfo) imageInfo = imageInfo[1];
	else {
		retVal.status = "ABORT";
		retVal.imgUrl = null;
		return retVal;
		}

	var iUrl = imageInfo.match(/"OriginalSrc":"(.+?)"/);
	if (!iUrl) iUrl = imageInfo.match(/"X2LargeSrc":"(.+?)"/);
	if (!iUrl) iUrl = imageInfo.match(/"XLargeSrc":"(.+?)"/);
	if (!iUrl) iUrl = imageInfo.match(/"MediumSrc":"(.+?)"/);
	if (!iUrl) iUrl = imageInfo.match(/"SmallSrc":"(.+?)"/);

	if (iUrl) {
		retVal.status = "OK";
		retVal.imgUrl = iUrl[1].replace(/\\\//g, "/");
		}
	else {
		retVal.status = "ABORT";
		retVal.imgUrl = null;
		}

	return retVal;
	}

