}

function(pageData, pageUrl) {
	var retVal = {};

	var ssIndex = pageData.match(/setStartImage\((.+)\)/);
	if(ssIndex) ssIndex = ssIndex[1];
	else {
		retVal.imgUrl = null;
		retVal.status = "ABORT";
		return retVal;
		}

	var ssImgList = pageData.match(/setProperty\(slImage, "src", ".+"\);/g);
	if (ssImgList) var iUrl = ssImgList[ssIndex];
	else {
		retVal.imgUrl = null;
		retVal.status = "ABORT";
		return retVal;
		}

	iUrl = iUrl.match(/"(http.+)"/);
	if (iUrl) iUrl = iUrl[1];
	else {
		retVal.imgUrl = null;
		retVal.status = "ABORT";
		return retVal;
		}

	retVal.imgUrl = iUrl;
	retVal.status = "OK";
	return retVal;
	}

