function(pageData, pageUrl) {
	var retVal = {};

	retVal.imgUrl = pageUrl.replace(/https?:\/\/www.sweetandhotgirls\.com\/img\.php\?img=(.+)/, "$1");
	retVal.status = "REQUEUE";

	return retVal;
	}

function(pageData, pageUrl) {
	var retVal = {};

	var req = new XMLHttpRequest();
	req.open("POST", pageUrl, false);
	req.setRequestHeader("Content-Type", "application/x-www-form-urlencoded; charset=UTF-8");
	var postData = "imgContinue=Continue+to+image+...+";
	req.send(postData);

	var someData = req.responseText;
	var iUrl = someData.match(/href=('|")(https?:\/\/[^/]+\/upload\/big\/.+?)\1/);
	if (!iUrl) iUrl = someData.match(/src=('|")(https?:\/\/[^/]+\/upload\/big\/.+?)\1/);
	if (!iUrl) {
		retVal.imgUrl = null;
		retVal.status = "ABORT";
		}
	else {
		retVal.imgUrl = iUrl[2];
		retVal.status = "OK";
		}
	return retVal;
}

