function(pageData, pageUrl) {
	var tmp = pageUrl.replace(/^https?:.+?(?=\/photo\/)/,'').replace(/\//g,'\\\/');
	if (tmp) var link = pageData.match(new RegExp("<a[^>]+?data-share-url=\"" + tmp+ "\"[^]+?>"));
	if (link) var iUrl = link.toString().match(/data-share-image="([^"]+)"/);
	return iUrl ? {imgUrl: iUrl[1], status: "OK"} : {imgUrl: null, status: "ABORT"};
}

function(pageData, pageUrl) {
	var retVal = {};
	retVal.imgUrl = pageUrl.replace(/.+\/(\d+)\/(\d+)\/.+\/(.+)\/$/g, "http://thechive.com/wp-content/uploads/$1/$2/$3.jpg");
	retVal.status = "OK";
	return retVal;
}

function(pageData, pageUrl) {
	var iUrl = pageData.match(/itemprop="image" src=('|")(https?:\/\/static.+?\.jpe?g)\1 title="(.+?)"/);
	return iUrl ? {imgUrl: iUrl[2], fileName: iUrl[3].replace(/\.jpe?g/i, ".jpg"), status: "OK"} : {imgUrl: null, status: "ABORT"}
}

function(pageData, pageUrl) {
	var retVal = {};

	var photo_div = pageData.match(/<div class="photoWrapper"(?:.|\s)+?<\/div>/);
	if (photo_div) {
		photo_div = photo_div[0];
		var iUrl = photo_div.match(/<a href="([^"]+)".+?<img src="([^"]+)"/);
		if (!iUrl) var directUrl = photo_div.match(/<img src="(.+?)"/);
	}

	if (directUrl) {
		retVal.imgUrl = directUrl[1];
		retVal.status = "OK";
		return retVal;
	}

	if (!iUrl) {
		retVal.imgUrl = null;
		retVal.status = "ABORT";
		}
	else {
		if (iUrl[1].search(/^https?:\/\/.+\.media\.tumblr\.com\/.+/) >= 0 ||
			iUrl[1].search(/^https?:\/\/www\.theselittlepix\.com\/photo\/\d+\/\d+\/.+/) >= 0) {
			retVal.imgUrl = iUrl[1];
			retVal.status = "OK";
			}
		else {
			retVal.imgUrl = iUrl[2];
			retVal.status = "OK";
			}
		}

	return retVal;
	}

