function(pageData, pageUrl) {
	var retVal = {imgUrl: null, status: "ABORT"};

	var iUrl = pageData.match(/<meta property="og:image" content="([^"]+)"/);
	if (!iUrl) iUrl = pageData.match(/src="(https?:\/\/[^\/]+\.tumblr\.com\/[^"]*tumblr_[^"]+_1280\.\w{3,4})"/);
	if (!iUrl) iUrl = pageData.match(/src="(https?:\/\/[^\/]+\.tumblr\.com\/[^"]*tumblr_[^"]+_500\.\w{3,4})"/);
	if (!iUrl) iUrl = pageData.match(/src="(https?:\/\/[^\/]+\.tumblr\.com\/[^"]*tumblr_[^"]+_400\.\w{3,4})"/);
	if (!iUrl) iUrl = pageData.match(/href="(https?:\/\/[^\/]+\.tumblr\.com\/photo\/\d+\/.+?)"/);
	if (!iUrl) iUrl = pageData.match(/href="(.+?)"[^>]+class="download image"/);
	if (iUrl) {
		retVal.imgUrl = iUrl[1];
		retVal.status = "OK";
	}

	return retVal;
}

function(pageData, pageUrl) {
	var retVal = {};

	var iUrl = pageData.match(/<img[^>]+?class="pic"[^>]+>/)[0];

	if (!iUrl) {
		retVal.imgUrl = null;
		retVal.status = "ABORT";
		}
	else {
		retVal.imgUrl = iUrl.match(/src="([^"]+)"/)[1];
		retVal.fileName = iUrl.match(/alt="([^"]+)"/)[1];
		retVal.status = "OK";
		}

	return retVal;
	}

