function(pageData, pageUrl) {
	var req = new XMLHttpRequest();
	req.open("POST", pageUrl, false);
	req.setRequestHeader("Content-Type", "application/x-www-form-urlencoded; charset=UTF-8");
	req.send("");

	return req.responseURL ?  {imgUrl: req.responseURL, status: "REQUEUE"} :  {imgUrl: null, status: "ABORT"};
}

function(pageData, pageUrl) {
	var retVal = {};

	var filtered = pageData.replace(/\n/g, " ");
	var frameElems = filtered.match(/<frame.+?>/ig);

	if (!frameElems) {
		var redirUrl = filtered.match(/<a href="(.+?)">/);
		newPageUrl = redirUrl[1];
		}
	else {
		var numElems = frameElems.length - 1;

		var newPageUrl = frameElems[numElems].match(/src(?:\s+)?=(?:\s+)?(\"|')?(.+?)\1/);

		if (newPageUrl) newPageUrl = newPageUrl[2];
		}

	if (!newPageUrl) {
		retVal.status = "ABORT";
		retVal.imgUrl = null;
		}
	else {
		retVal.status = "REQUEUE";
		retVal.imgUrl = newPageUrl;
		}
	return retVal;
	}

