	var rUrl = pageData.match(/redirect_url="(.+?)"/);
	return {imgUrl: rUrl[1], status: "REQUEUE"};
}

function(pageData, pageUrl) {
	var iUrl = pageData.match(/img src=("|')(.+)\1 class=("|')pic\3 alt=("|')(.+)\4 onLoad=["|']/);
	return iUrl ? {imgUrl: iUrl[2], fileName: iUrl[5], status: "OK"} : {imgUrl: null, status: "ABORT"};
}

function(pageData, pageUrl) {
	var id = pageUrl.match(/-?\d+_\d+/);
	var pDat = pageData.match("\\{\"id\":\"" + id + "\".+?(?:,\"(\\w_)src\":\"([^\"]+)\",\"\\1\":\\[[^\\]]+\\])+\\}");
	var iUrl = null;
	["x","y","z"].forEach(function(srcVal) {
		let tUrl = pDat[0].match("\"" + srcVal + "_src\":\"([^\"]+)\"");
		if (tUrl) iUrl = tUrl[1];
	});
	return iUrl ? {imgUrl: iUrl.replace(/\\\//g,"/"), status: "OK"} : {imgUrl: null, status: "ABORT"};
}

function(pageData, pageUrl) {
	var retVal = {};

	var iUrl = pageUrl.match(/http.+url=(.+)/);
	iUrl = iUrl[1];
	iUrl = iUrl.replace(/%3A/gi, ":");
	iUrl = iUrl.replace(/%2F/gi, "/");
	iUrl = iUrl.replace(/%3F/gi, "?");
	iUrl = iUrl.replace(/%3D/gi, "=");

	if (!iUrl) {
		retVal.imgUrl = null;
		retVal.status = "ABORT";
		}
	else {
		retVal.imgUrl = iUrl;
		retVal.status = "REQUEUE";
		}

	return retVal;
	}

