			retVal.imgUrl = null;
			retVal.status = "ABORT";
			}
		else {
			retVal.imgUrl = iUrl[1];
			retVal.status = "OK";
			}

		return retVal;
		}

	iUrl = pageData.match(/class="mo-fullsize"><a href="(.+?)"/);

	if (!iUrl) {
		retVal.imgUrl = null;
		retVal.status = "ABORT";
		}
	else {
		retVal.imgUrl = iUrl[1];
		retVal.status = "REQUEUE";
		}

	return retVal;
	}

