function(pageData, pageUrl) {
	var retVal = {imgUrl: null, status: "ABORT"};

	var iUrl = pageData.match(/<div class="fullMedia"><a href="([^"]+)"/);
	if (!iUrl) iUrl = pageData.match(/Size of this preview.+?href="(.+?)"/);
	if (!iUrl) iUrl = pageData.match(/No higher resolution available.+?href="(.+?)"/);

	if (iUrl) {
		retVal.imgUrl = iUrl[1];
		retVal.status = "OK";

		try {
			retVal.fileName = decodeURIComponent(iUrl[1].match(/\/([^/]+)$/)[1]);
		}
		catch(e) {
			retVal.fileName = Math.random().toString().substring(2) + ".jpg";
		}
	}

	return retVal;
}

