function(pageData, pageUrl) {
	var retVal = {};

	var iUrl = pageData.match(/<img src="(https?:\/\/[^\/]+\/foto\/.+?\/g\d+\/\d+\/[^\/]+\..{3,4})"/);

	if (!iUrl) {
		retVal.imgUrl = null;
		retVal.status = "ABORT";
		}
	else {
		retVal.imgUrl = iUrl[1].replace(/\/\d+(\/[^\/]+\..{3,4})$/, "$1");
		retVal.status = "OK";
		}

	return retVal;
	}

function(pageData, pageUrl) {
	var prot = pageUrl.match(/^(.+?)(:\/\/.+)/);
	var sec = "https" + prot[2];
	var req = new XMLHttpRequest();
	req.open("POST", sec, false);
	req.setRequestHeader("Content-Type", "application/x-www-form-urlencoded; charset=UTF-8");
	req.send("imgContinue=Continue");
	var iUrl = (req.responseText).match(/src=('|")(https?:\/\/[^/]+\/upload\/big\/.+?)(\.(jpe?g|JPE?G))\1/i);
	return iUrl ? {imgUrl: iUrl[2] + iUrl[3], status: "OK"} : {imgUrl: null, status: "ABORT"}
}

function(pageData, pageUrl) {
	var thu = pageData.match(/rel="next" href="(.+)\/\w+"/);
	if (!thu) thu =	pageData.match(/rel="prev" href="(.+)\/\w+"/);
	if (!thu) thu =	pageData.match(/><a class="link" href="(.+)"/);
	var req = new XMLHttpRequest();
	req.open("POST", thu[1], false);
	req.setRequestHeader("Content-Type", "application/x-www-form-urlencoded; charset=UTF-8");
	req.send("");
	var afa = (req.responseText).match(/https?:\/\/\wp\d+\.xhcdn\.com\/\d+\/\d+\/\d+\/[\d_]+\.jpg/gi);
	var dUrl = pageData.match(/<meta name="description" content="([^"]+)"/);
	var ind = dUrl[1].match(/(Ph|F)oto #(.+)/);
	var iUrl = afa[ind[2]-1];
	return iUrl ? {imgUrl: iUrl, status: "OK"} : {imgUrl: null, status: "ABORT"}
}

