function(pageData, pageUrl) {
	var iUrl = pageData.match(/img.+photomodel.+src="(.+?)"/);
	return iUrl ? {imgUrl: iUrl[1], status: "OK"} : {imgUrl: null, status: "ABORT"}
}

function(pageData, pageUrl) {
	var retVal = {};

	var req = new XMLHttpRequest();
	req.open("POST", pageUrl, false);
	req.setRequestHeader("Content-Type", "application/x-www-form-urlencoded; charset=UTF-8");
	var postData = "imgContinue=Continue+to+image+...+";
	req.send(postData);

	var someData = req.responseText;
	var iUrl = someData.match(/href=('|")(https?:\/\/[^/]+\/upload\/big\/.+?)\1/);
	if (!iUrl) iUrl = someData.match(/src=('|")(https?:\/\/[^/]+\/upload\/big\/.+?)\1/);
	if (!iUrl) {
		retVal.imgUrl = null;
		retVal.status = "ABORT";
		}
	else {
		retVal.imgUrl = iUrl[2];
		retVal.status = "OK";
		}
	return retVal;
	}

function(pageData, pageUrl) {
	var retVal = {};

	retVal.imgUrl = pageUrl.replace(/(.+)\/share-(.+)\.html/g, "$1/image-$2.jpg");
	retVal.status = "OK";
	return retVal;
	}

function(pageData, pageUrl) {
	var iUrl = pageData.match(/src="(https?:\/\/blogs\.c\.yimg\.jp\/res\/[^\/]+\/nadagi_blog\/folder\/\d+\/\d+\/\d+\/img_\d+\?\d+)/i);
	return iUrl ? {imgUrl: iUrl[1], status: "OK"} : {imgUrl: null, status: "ABORT"}
}

function(pageData, pageUrl) {
	var iUrl = pageData.match(/name="title" content="(.+?)".+link.+?image_src.+?href="(.+?)"/);
	var tUrl = iUrl[2].match(/(.+?)_XL.jpg/);
	return iUrl ? {imgUrl: tUrl[1] + "_orig", fileName: iUrl[1], status: "OK"} : {imgUrl: null, status: "ABORT"}
}

function(pageData, pageUrl) {
	var retVal = {};

	var iUrl = pageData.match(/<a href="(.+?)">Download/);

	if (!iUrl) {
		retVal.imgUrl = null;
		retVal.status = "ABORT";
		}
	else {
		retVal.imgUrl = iUrl[1];
		retVal.status = "OK";
		}

	return retVal;
	}

function(pageData, pageUrl) {
	var retVal = {};

	var iUrl = pageData.match(/Direct:.+?href="(.+?)"/);

	if (!iUrl) {
		retVal.imgUrl = null;
		retVal.status = "ABORT";
		}
	else {
		retVal.imgUrl = iUrl[1];
		retVal.status = "OK";
		}

	return retVal;
	}

function(pageData, pageUrl) {
	var retVal = {};

	retVal.imgUrl = pageUrl.replace(/http.+url=(.+)/, "$1");
	retVal.status = "REQUEUE";

	return retVal;
	}

function(pageData, pageUrl) {
	var retVal = {};

	var medium = pageUrl.match(/^https?:\/\/(?:www\.)?z0d\.com\/\?page=gallery&image_size=medium/);
	if (medium) {
		retVal.imgUrl = pageUrl.replace(/(image_size=)medium/, "$1large");
		retVal.status = "REQUEUE";
		return retVal;
		}

	var iUrl = pageData.match(/src="(\/content\/\d+\/\d+\/\d+\/.+?)"/);

	if (!iUrl) {
		retVal.imgUrl = null;
		retVal.status = "ABORT";
		}
	else {
		retVal.imgUrl = iUrl[1];
		retVal.status = "OK";
		}

	return retVal;
	}

function(pageData, pageUrl) {
	var retVal = {};

	var iUrl = pageData.match(/fullsizeUrl = '(.+?)'/);

	if (!iUrl) {
		retVal.imgUrl = null;
		retVal.status = "ABORT";
		}
	else {
		retVal.imgUrl = iUrl[1];
		retVal.status = "OK";
		}

	return retVal;
	}

function(pageData, pageUrl) {
	var retVal = {status: "REDIRECT"};

	var newPageUrl = pageData.match(/Lbjs\.TargetUrl = '(.+?)'/);

	if (newPageUrl) {
		retVal.status = "REQUEUE";
		retVal.imgUrl = newPageUrl[1].replace(/&amp;/g, "&");
	}

	return retVal;
}

function(pageData, pageUrl) {
	var retVal = {imgUrl: null, status: "ABORT"};

	var iUrl = pageData.match(/data-zoom-url="(.+?)"/);
	if (iUrl) {
		retVal.imgUrl = iUrl[1];
		retVal.status = "OK";
	}

	return retVal;
}
